mapper.select:
SELECT 

FROM yp_cardtype a

WHERE a.id = #{id}

SELECT 

FROM yp_cardtype a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.area = #{area}


		AND a.cardtype = #{cardtype}


		AND a.term = #{term}


		AND a.equity = #{equity}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM yp_cardtype a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE yp_cardtype SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
