mapper.select:
SELECT 

FROM yp_member a

WHERE a.id = #{id}

SELECT 

FROM yp_member a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.carduser LIKE 
			'%'||#{carduser}||'%'
			'%'+#{carduser}+'%'
			concat('%',#{carduser},'%')


		AND a.sex = #{sex}


		AND a.phone = #{phone}


		AND a.idcard = #{idcard}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM yp_member a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE yp_member SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
