mapper.select:
SELECT 

FROM yp_rock_hall a

WHERE a.id = #{id}

SELECT 

FROM yp_rock_hall a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.region = #{region}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.status = #{status}


		AND a.type = #{type}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM yp_rock_hall a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE yp_rock_hall SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
